<template>
    <div>
        <h2>Login</h2>
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="username">Username</label>
                <input type="text" v-model="username" name="username" class="form-control" :class="{ 'is-invalid': submitted && !username }" />
                <div v-show="submitted && !username" class="invalid-feedback">Username is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div>
            <div class="form-group">
                <!-- <button class="btn btn-primary" :disabled="status.loggingIn">Login</button>
                <img v-show="status.loggingIn" src="data:image/gif;base64," /> -->
                <button class="btn btn-primary" :disabled='!submitted'>Login</button>
                <router-link to="/register" class="btn btn-link">Register</router-link>
            </div>
        </form>
    </div>
</template>

<script>
import {LoginService} from '../services/loginRoutine'
const api = new LoginService()
export default {
  data () {
    return {
      'username': null,
      'password': null,
      res: null
    }
  },
  methods: {
    login () {
      console.log('logging in...')
      api.login(this.username, this.password).then(data => {
        console.log(data)
        this.res = data
      })
    },
    handleSubmit (e) {
      console.log('in handleSubmit')
      this.submitted = true
      const { username, password } = this
      if (username && password) {
        this.login()
      }
    }
  },
  computed: {
    submitted: {
      get () {
        if (this.username && this.password) {
          return true
        }
        return false
      },
      set () {
        return this.username && this.password
      }
    }
  }
}
</script>